import React, { useState, useEffect } from 'react';

function OTPForm() {
  const [otpCode, setOTPCode] = useState('');
  const [timer, setTimer] = useState(60);

  useEffect(() => {
    let intervalId;
    if (timer > 0) {
      intervalId = setInterval(() => {
        setTimer(prevTimer => prevTimer - 1);
      }, 1000);
    }
    return () => clearInterval(intervalId);
  }, [timer]);

  const handleOTPCodeChange = event => {
    setOTPCode(event.target.value);
  };

  const handleSubmit = event => {
    event.preventDefault();
    // Call your verification function here with otpCode as the parameter
    // If verification is successful, disable the input and button
  };
  const otpv = () =>{}
  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="otpCode">Enter OTP code:</label>
      <input
        type="text"
        id="otpCode"
        name="otpCode"
        value={otpCode}
        onChange={handleOTPCodeChange}
        disabled={timer === 0}
      />
      <button type="submit" disabled={otpCode.length !== 6 || timer === 0}>
        Verify OTP
      </button>
      <p>Time remaining: {timer} seconds</p>
    </form>
  );
}
