import {Link} from 'react-router-dom';
import './Login.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import GoogleIcon from '@mui/icons-material/Google';
import FacebookIcon from '@mui/icons-material/Facebook';
import { useNavigate } from 'react-router-dom';

const Login = () => {
   const [idPass, setIdPass] = useState({
      email : "",
      pass : ""
   })

   const [data, setData] = useState();
   const navigate = useNavigate(); 
   const store = (event) => {
      const {name, value} = event.target;
      setIdPass( (oldVal) => {
          return {
            ...oldVal,
            [name] : value
          }
      })
   }
    return (
        <>     
              <div className="container" >
                    <div className="row justify-content-center" style={{marginTop:"150px"}}>
                           <div className=" col-4 box">
                              <form onSubmit={loginSuccessFull} style={{backgroundColor:"#fff"}}>
                                    <h1 className="S">Sign in</h1>
                                    <h6 className="U">Email</h6>
                                    <input className="I1 form-control" type="text" placeholder="Type your e-mail" name="email" onChange={store} value = {idPass.email} autoComplete="on" />
                                    <h6 className="U">Password</h6>
                                    <input className="I2 form-control" type="password" placeholder="Type your password" name="pass" onChange={store} value = {idPass.pass} autoComplete="on" />
                                    
                                    <Link style={{marginLeft: "0px",marginTop:"-10px",backgroundColor:"#fff",textDecoration :"none"}}exact to="/forgetpassword">forget password</Link> 
                                    
                                    <br />
                                    <Button className="B" variant="primary" type="submit" onSubmit={loginSuccessFull} >Login</Button>
                                    <br />
                                    <a href="https://mail.google.com" target="Gmail" style={{backgroundColor:"#fff"}}><GoogleIcon className='G'/></a>
                                    <a href="https://www.facebook.com/" target="Facebook" style={{backgroundColor:"#fff"}}><FacebookIcon style={{marginTop: "15px",backgroundColor:"#fff"}}/></a>
                                    <h6 className='Do'>Don't have an account ? <span ><Link exact to="/register" style={{backgroundColor:"#fff",textDecoration :"none"}}>Register</Link></span></h6>
                              </form>
                           </div>   
                           
                    </div>
                    </div>
                    
          
        </>
    )
}

export default Login;
