import React, { useState } from "react";
import './Emailv.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import EmailIcon from '@mui/icons-material/Email';
import sgMail from '@sendgrid/mail';

sgMail.setApiKey(process.env.SENDGRID_API_KEY);

const Emailv = () =>{
    const [email, setEmail] = useState();

    const getEmail = (e) => {
        setEmail(e.tar)
    }
    
    const sendEmail = async () => {
        const msg = {
          to: '',
          from: 'sender@example.com',
          subject: 'Example Subject',
          text: 'Example content',
        };
        try {
          await sgMail.send(msg);
          console.log('Email sent successfully');
        } catch (error) {
          console.error(error);
        }
      };


    return(
        <>
        <div className="container ">
            
            <div className="row justify-content-center">
                <div className="col-5 box" style={{marginTop:"150px", padding:"80px"}}>
                        <h1 className="title" >Get Started</h1>
                        <p className="Para">Please enter your details</p>
                        {/* <EmailIcon/> */}
                        <input className="form-control email-form " type="email" placeholder="Enter your email" value={email} onChange={getEmail}  />
                        <button onClick={sendEmail} class="btn btn-secondary form-control button-cs" type="button">Sign up</button>
                </div>

            </div>
        </div>
        
        </>
    )
}

export default Emailv;
